import {ADD_QUANTITY, ADD_TO_CART, REMOVE_ITEM, SUB_QUANTITY} from "./action-types/cart-actions";

export const addToCart = (addedItems) => {
    return (dispatch, getState, {getFirebase, getFirestore}) => {
        const firestore = getFirestore();
        let state = getState();
        console.log("cartActions: add to cart");
        console.log(state.cart.items);
        let addedItem = state.cart.items.find(item => item.id === addedItems.id);
        let existedItem = state.cart.addedItems.find(item => addedItems.id === item.id);
        // console.log(state.addedItems);
        if (existedItem) {
            addedItem.quantity += 1;
            firestore.collection('carts').add({
                addedItems,
                customerFirstName: 'Raj',
                customerLastName: 'Koothrappali',
                customerId: 12345,
                shoppedAt: new Date()
            }).then(() => {
                dispatch({type: ADD_TO_CART, addedItems});
            }).catch(err => {
                dispatch({type: "ADD_TO_CART_ERROR", err});
            })
            return {
                ...state,
                total: state.total + addedItem.price
            }
        } else {
            addedItem.quantity = 1;
            firestore.collection('carts').add({
                addedItems,
                customerFirstName: 'Raj',
                customerLastName: 'Koothrappali',
                customerId: 12345,
                shoppedAt: new Date()
            }).then(() => {
                dispatch({type: ADD_TO_CART, addedItems});
            }).catch(err => {
                dispatch({type: "ADD_TO_CART_ERROR", err});
            })
            return {
                ...state,
                addedItems: [...state.cart.addedItems, addedItem],
                total: state.total + addedItem.price
            }
        }


    }
};

