import React from "react";
import {connect} from "react-redux";
import {removeItem, addQuantity, subtractQuantity} from "../actions/cartActions";
import Recipe from "../Recipe";

class Cart extends React.Component {

    handleRemove = (id) => {
        this.props.removeItem(id);
    };

    handleAddQuantity = (addedQuantityItem) => {
        this.props.addQuantity(addedQuantityItem);
    };

    handleSubtractQuantity = (subtractQuantityItem) => {
        this.props.subtractQuantity(subtractQuantityItem);
    };

    render() {
        const addedItemsTemp = this.props.addedItems ? this.props.addedItems : {};
        console.log('addedItems:', this.props.addedItems);
        if (addedItemsTemp.length > 0 ) {
            let addedItems = addedItemsTemp.map(item => {
                console.log('item:', item);
                // console.log("quantity:"+item.quantity);
                return (
                    <li className="list-group-item" key={item.addedItems.id}>
                        <div className="item-img">
                            <img src={item.addedItems.img} alt={item.addedItems.img}/>
                        </div>
                        <div className="item-desc">
                            <span className="item-title">{item.addedItems.title}</span>
                            <p>{item.addedItems.description}</p>
                            <p><b>price: {item.addedItems.price}</b></p>
                            <p><b>Quantity: {item.addedItems.quantity}</b></p>
                            {/*<BrowserRouter>*/}
                            {/*    <Link to="/cart"><i className="fas fa-sort-up" onClick={()=>{*/}
                            {/*        this.handleAddQuantity(item.id)*/}
                            {/*    }}></i></Link>*/}
                            {/*    <Link to="/cart"><i className="fas fa-sort-down" onClick={()=>{*/}
                            {/*        this.handleSubtractQuantity(item.id)*/}
                            {/*    }}></i></Link>*/}
                            {/*</BrowserRouter>*/}
                        </div>
                        <button onClick={() => {
                            this.handleAddQuantity(item)
                        }}>add
                        </button>
                        <button onClick={() => {
                            this.handleSubtractQuantity(item)
                        }}>sub
                        </button>
                        <button onClick={() => {
                            this.handleRemove(item.id)
                        }}>Remove
                        </button>
                    </li>
                )
            })
            return (
                <div className="container">
                    <div className="cart">
                        <h5>You have ordered:</h5>
                        <ul className="list-group">
                            {addedItems}
                        </ul>
                    </div>
                    <Recipe/>
                </div>
            )
        } else {
            return (
                <p>Nothing</p>
            )

        }
        // let addedItems = this.props.addedItems.length ?
        // (



    }
}

const mapStateToProps = (state) => {
    // console.log("Cart.js");
    console.log('cart state:', state);
    return {
        addedItems: state.firestore.ordered.carts
        // addedItems: state.cart.addedItems
        // quantity: state.firestore.cart.quantity
    }
};

const mapDispatchToProps = (dispatch) => {
    return {
        removeItem: id => {
            dispatch(removeItem(id))
        },
        addQuantity: addQuantityItem => {
            dispatch(addQuantity(addQuantityItem))
        },
        subtractQuantity: subtractQuantityItem => {
            dispatch(subtractQuantity(subtractQuantityItem))
        }
    }
};

export default connect(mapStateToProps, mapDispatchToProps)(Cart);
