            console.log(state);

            // state.cart.total = state.cart.total + addedItems.price;
            let itemToAdd = state.cart.items.find(item => item.id === addedItems.id);
            if (itemToAdd) {
                addedItems.quantity = 1;
                firestore.collection('carts').add({
                    addedItems,
                    customerFirstName: profile.firstName,
                    customerLastName: profile.lastName,
                    customerId: userId,
                    shoppedAt: new Date()
                }).then(() => {
                    dispatch({type: ADD_TO_CART, addedItems});
                }).catch(err => {
                    dispatch({type: "ADD_TO_CART_ERROR", err});
                });
            } else {
                addedItems.quantity += 1;
                firestore.collection('carts').doc(firestore.data.currentUser.uid).update({
                    addedItems,
                    updatedAt: new Date()
                }).then(() => {
