import React from "react";
import {connect} from "react-redux";
import {firestoreConnect} from "react-redux-firebase";
import {compose} from "redux";

const ItemDetails = (props) => {
    const id = props.match.params.id;
    console.log("details");
    console.log(props);
        return (
            <div className="container">
                <h3 className="text-center">Item details - {id}</h3>
                {/*<div className="row">*/}
                {/*    <div className="card col-md-3" key={id}>*/}
                        <img className="card-img-top" src={props.item.img} alt={props.item.title}/>
                        <div className="card-body">
                            <h5 className="card-title">{props.item.title}</h5>
                            <p className="card-text">{props.item.description}</p>
                            <p className="card-text"><b>Price: CAD{props.item.price}</b></p>
                        </div>
                {/*    </div>*/}
                {/*</div>*/}
            </div>
        )
    }

const mapStateToProps = (state, ownProps) => {
    console.log(state);
    const id = ownProps.match.params.id;
    const items = state.cart.items;
    const item = items?items[id]:null;
    console.log(item);
    return {
        item: item
    }
}

export default compose(
    connect(mapStateToProps),
    firestoreConnect([
        {collection: 'cart'}
    ])
)(ItemDetails);

