        const firestore = getFirestore();
        firestore.collection('carts').doc(id).delete().then(() => {
            console.log("deleted");
            dispatch({type: REMOVE_ITEM, id});
        }).catch(err => {
            console.log('delete failed');
            dispatch({type: "REMOVE_ITEM_ERROR", err});
        })
    }
};

