            quantity: 0
        },
        {
            id: 6,
            title: "Black",
            description: "Lorem ipsum dolor sit amet consectetur adipisicing elit. Minima, ex.",
            price: 280,
            img: Item6,
            quantity: 0
        }
    ],
    addedItems: [],
    total: 0
};

const cartReducer = (state = initState, action) => {
    switch (action.type) {
        case ADD_TO_CART:
            // console.log('action.addeditems:',  state);
            return {
                ...state,
                addedItems: [...state.cart.addedItems, action.addedItems],
                total: state.total + action.addedItems.price
                // addedItems: action.addedItems
            };
        case REMOVE_ITEM:
            return {
                ...state
            };
        case ADD_QUANTITY:
            return {
                ...state
            };
        default:
            return state;
    }




    if (action.type === REMOVE_ITEM) {
        let itemToRemove = state.items.find(item => item.id === action.id);
        let newItems = state.addedItems.filter(item => action.id !== item.id);
        let newTotal = state.total - (itemToRemove.price) * itemToRemove.quantity;
        if (itemToRemove) {
            return {
                ...state,
                addedItems: newItems,
                total: newTotal
            }
        }
    }

    if (action.type === ADD_QUANTITY) {
        let itemToAdd = state.items.find(item => item.id === action.id);
        console.log("cartReducer: add quantity")
        console.log(itemToAdd);
        if (itemToAdd) {
            return {
                ...state,
                quantity: ++itemToAdd.quantity,
                total: state.total + itemToAdd.price
            }
        }
    }

    if (action.type === SUB_QUANTITY) {
        let itemToSub = state.items.find(item => item.id === action.id);
        if (itemToSub.quantity === 1) {
            let newItems = state.items.filter((item => item.id === action.id));
            let newTotal = state.total - itemToSub.price;
            return {
                ...state,
                addedItems: newItems,
                total: newTotal
            }

        } else {
            return {
                ...state,
                quantity: --itemToSub.quantity,
                total: state.total - itemToSub.price
            }
        }
    }
    if (action.type === ADD_SHIPPING) {
        return {
            ...state,
            total: state.total + 6
        }
    }

    if (action.type === SUB_SHIPPING) {
        return {
            ...state,
            total: state.total - 6
        }
    } else {
        return state;
    }
};

export default cartReducer;
