import React from "react";
import {connect} from "react-redux";
import {removeItem, addQuantity, subtractQuantity} from "./actions/cartActions";
import Recipe from "./Recipe";

class Cart extends React.Component {

    handleRemove = (removedItem) => {
        this.props.removeItem(removedItem);
    }

    handleAddQuantity = (addedQuantityItem) => {
        this.props.addQuantity(addedQuantityItem);
    }

    handleSubtractQuantity = (subtractQuantityItem) => {
        this.props.subtractQuantity(subtractQuantityItem);
    }

    render() {
        console.log("**** Cart.js before return ****")
        let addedItems = this.props.addedItems.length ?
            (
                this.props.addedItems.map(item => {
                    console.log("item");
                    console.log(item);
                    console.log("******** cart.js in return ****");
                    console.log("quantity:"+item.quantity);
                    return (
                                <li className="list-group-item" key={item.id}>
                                    <div className="item-img">
                                        <img src={item.img} alt={item.img}/>
                                    </div>
                                    <div className="item-desc">
                                        <span className="item-title">{item.title}</span>
                                        <p>{item.description}</p>
                                        <p><b>price: {item.price}</b></p>
                                        <p><b>Quantity: {item.quantity}</b></p>
                                        {/*<BrowserRouter>*/}
                                        {/*    <Link to="/cart"><i className="fas fa-sort-up" onClick={()=>{*/}
                                        {/*        this.handleAddQuantity(item.id)*/}
                                        {/*    }}></i></Link>*/}
                                        {/*    <Link to="/cart"><i className="fas fa-sort-down" onClick={()=>{*/}
                                        {/*        this.handleSubtractQuantity(item.id)*/}
                                        {/*    }}></i></Link>*/}
                                        {/*</BrowserRouter>*/}
                                    </div>
                                    <button onClick={()=>{this.handleAddQuantity(item)}}>add</button>
                                    <button onClick={()=>{this.handleSubtractQuantity(item)}}>sub</button>
                                    <button onClick={()=>{this.handleRemove(item)}}>Remove</button>
                                </li>
                        )
                    })
            ) : (
                <p>Nothing</p>
            );
        return (
            <div className="container">
                <div className="cart">
                    <h5>You have ordered:</h5>
                    <ul className="list-group">
                    {addedItems}
                    </ul>
                </div>
                <Recipe />
            </div>
        )
    }
}

const mapStateToProps =(state) => {
    console.log(state);
    return {
        addedItems: state.firestore.ordered.cart||state.addedItems,
        quantity: state.cart.quantity
    }
};

const mapDispatchToProps = (dispatch) => {
    return {
        removeItem: removedItem => {dispatch(removeItem(removedItem))},
        addQuantity: addQuantityItem => {dispatch(addQuantity(addQuantityItem))},
        subtractQuantity: subtractQuantityItem => {dispatch(subtractQuantity(subtractQuantityItem))}
    }
};

export default connect(mapStateToProps, mapDispatchToProps)(Cart);
